import React from 'react';

const ColorfulMessage = (props) => {
    const { color, children } = props;

    const contentStyle = {
        color: props.color,
        fontSize: '18px',
        fontWeight: 'bold'
    };

    return (
        <p style={contentStyle}>{props.message}</p>
    );
};

export default ColorfulMessage;
